mapper.select:
select * from board where articleno in 
	(select * from 
		(select b.articleno from board b, reply r
			where b.articleno = r.articleno and DATE_FORMAT(r.regtime, "%Y-%m-%d") = curdate()
			group by b.articleno
			order by count(r.replyid)
			limit 3
		) t
    );

mapper.insert:
insert into board(
	title, writer, content
) values (
	#{title}, #{writer}, #{content}
)

mapper.update:
update board 
   set title = #{title}, 
   	   writer = #{writer}, 
   	   content = #{content}
 where articleno = #{articleno}

mapper.insert:
insert into likey(
	userid, articleno
) values (#{userid}, #{articleno})

mapper.update:
update board
	set likey = likey + 1
where articleno = #{articleno}

mapper.delete:
delete 
  from likey 
 where userid = #{userid} and articleno = #{articleno}

mapper.update:
update board
	set likey = likey - 1
where articleno = #{articleno}
